// src/components/DisplayRecords.js
import React, { useState } from 'react';
import axios from 'axios';

const DisplayRecords = () => {
  const [wins, setWins] = useState(0);
  const [records, setRecords] = useState([]);

  const fetchRecords = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/fetch",dataToSend); 
      setRecords(response.data);
    } catch (error) {
      alert('Error fetching records!');
    }
  };

  return (
    <div>
      <h2>Top Teams by Wins</h2>
      <input type="number" placeholder="Minimum Wins" onChange={(e) => setWins(e.target.value)} />
      <button onClick={fetchRecords}>Fetch Records</button>
      {records.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Team</th>
              <th>Games Played</th>
              <th>Win</th>
              <th>Draw</th>
              <th>Loss</th>
              <th>Goals For</th>
              <th>Goals Against</th>
              <th>Points</th>
              <th>Year</th>
            </tr>
          </thead>
          <tbody>
            {records.map((record, index) => (
              <tr key={index}>
                <td>{record.team}</td>
                <td>{record.gamesPlayed}</td>
                <td>{record.win}</td>
                <td>{record.draw}</td>
                <td>{record.loss}</td>
                <td>{record.goalsFor}</td>
                <td>{record.goalsAgainst}</td>
                <td>{record.points}</td>
                <td>{record.year}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default DisplayRecords;
