import React, { useState } from 'react';
import axios from 'axios';

const AddData = () => {
  const [formData, setFormData] = useState({
    team: '',
    gamesPlayed: 0,
    win: 0,
    draw: 0,
    loss: 0,
    goalsFor: 0,
    goalsAgainst: 0,
    points: 0,
    year: 0,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Match frontend field names to backend
      const dataToSend = {
        team: formData.team,
        gamesPlayed: formData.gamesPlayed,
        win: formData.win,
        draw: formData.draw,
        loss: formData.loss,
        goalsFor: formData.goalsFor,
        goalsAgainst: formData.goalsAgainst,
        points: formData.points,
        year: formData.year,
      };

      // Send POST request to /api/add
      const response = await axios.post('http://localhost:5000/api/add', dataToSend);
      setMessage(response.data.message || 'Data added successfully!');
    } catch (error) {
      setMessage('Error adding data: ' + (error.response?.data?.message || error.message));
      console.error(error);
    }
  };
  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Team Data</h2>
      <input name="team" placeholder="Team" onChange={handleChange} required />
      <input name="gamesPlayed" placeholder="Games Played" type="number" onChange={handleChange} />
      <input name="win" placeholder="Win" type="number" onChange={handleChange} />
      <input name="draw" placeholder="Draw" type="number" onChange={handleChange} />
      <input name="loss" placeholder="Loss" type="number" onChange={handleChange} />
      <input name="goalsFor" placeholder="Goals For" type="number" onChange={handleChange} />
      <input name="goalsAgainst" placeholder="Goals Against" type="number" onChange={handleChange} />
      <input name="points" placeholder="Points" type="number" onChange={handleChange} />
      <input name="year" placeholder="Year" type="number" onChange={handleChange} />
      <button type="submit">Add Data</button>
    </form>
  );
};

export default AddData;
