// src/components/DeleteData.js
import React, { useState } from 'react';
import axios from 'axios';

const DeleteData = () => {
  const [team, setTeam] = useState('');

  const handleDelete = async () => {
    try {
      await axios.post(`/api/delete/${team}`); // Replace with your actual API endpoint
      alert(`Record for team ${team} deleted successfully!`);
    } catch (error) {
      alert('Error deleting record!');
    }
  };

  return (
    <div>
      <h2>Delete Team Data</h2>
      <input type="text" placeholder="Team Name" onChange={(e) => setTeam(e.target.value)} />
      <button onClick={handleDelete}>Delete Record</button>
    </div>
  );
};

export default DeleteData;
