// src/components/ShowStats.js
import React, { useState } from 'react';
import axios from 'axios';

const ShowStats = () => {
  const [year, setYear] = useState('');
  const [stats, setStats] = useState(null);

  const fetchStats = async () => {
    try {
      const response = await axios.get(`/api/stats/${year}`); // Replace with your actual API endpoint
      setStats(response.data);
    } catch (error) {
      alert('Error fetching stats!');
    }
  };

  return (
    <div>
      <h2>Team Stats</h2>
      <input type="number" placeholder="Year" onChange={(e) => setYear(e.target.value)} />
      <button onClick={fetchStats}>Get Stats</button>
      {stats && (
        <div>
          <p>Total Games Played: {stats.totalGames}</p>
          <p>Total Wins: {stats.totalWins}</p>
          <p>Total Draws: {stats.totalDraws}</p>
        </div>
      )}
    </div>
  );
};

export default ShowStats;
