import React, { useState } from 'react';
import axios from 'axios';
import './update.css';

const UpdateData = () => {
  const [team, setTeam] = useState('');
  const [formData, setFormData] = useState({
    gamesPlayed: 0,
    win: 0,
    draw: 0,
    loss: 0,
    goalsFor: 0,
    goalsAgainst: 0,
    points: 0,
    year: 0,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`/api/update/${team}`, formData); // Replace with your actual API endpoint
      alert('Data updated successfully!');
    } catch (error) {
      alert('Error updating data!');
    }
  };

  return (
    <div className="update-container">
      <form onSubmit={handleSubmit}>
        <h2>Update Team Data</h2>
        <input
          placeholder="Team Name"
          value={team}
          onChange={(e) => setTeam(e.target.value)}
          required
        />
        <input name="gamesPlayed" placeholder="Games Played" type="number" onChange={handleChange} />
        <input name="win" placeholder="Win" type="number" onChange={handleChange} />
        <input name="draw" placeholder="Draw" type="number" onChange={handleChange} />
        <input name="loss" placeholder="Loss" type="number" onChange={handleChange} />
        <input name="goalsFor" placeholder="Goals For" type="number" onChange={handleChange} />
        <input name="goalsAgainst" placeholder="Goals Against" type="number" onChange={handleChange} />
        <input name="points" placeholder="Points" type="number" onChange={handleChange} />
        <input name="year" placeholder="Year" type="number" onChange={handleChange} />
        <button type="submit">Update Data</button>
      </form>
    </div>
  );
};

export default UpdateData;
